import "./Chat.css";
import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import { io } from "socket.io-client";

export default function Chat() {
  const [data, setData] = useState("");
  const [students, setStudents] = useState([]);
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [finalmessage, setFinalMessage] = useState("");
  const socket = io("http://localhost:9000");

  useEffect(() => {
    Promise.all([
      axios.get("http://localhost:9000/api/data"),
      axios.get("http://localhost:9000/api/students"),
    ])
      .then(([data, studentData]) => {
        setData(data.data.message);
        setStudents(studentData.data);
      })
      .catch((error) => console.log("Error fetching data:", error));

    // socket.on("receiveMessage", (message) => {
    //   console.log("Message received from socket:", message);

    // });

    return () => {
      socket.disconnect();
    };
  }, []);

  const HandleMessage = (event) => {
    setMessage(event.target.value);
  };

  const HandleSubmit = (event) => {
    event.preventDefault();
    console.log("Message sent:", message);

    axios
      .post("http://localhost:9000/api/message", { message })
      .then((response) => {
        console.log("Message sent succefullt:", response.data);
      })
      .catch((error) => {
        console.log("Error sending message:", error);
      });
    socket.emit("sendMessage", message);
    setFinalMessage(message);
    setMessages((prevMessages) => [...prevMessages, message]);
    setMessage("");
    console.log(messages);
  };

  return (
    <div className="main">
      <div className="msgBody">
        <p>{finalmessage}</p>
        <ul>
          {messages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul>
      </div>
      <form onSubmit={HandleSubmit}>
        <input
          onChange={HandleMessage}
          type="text"
          placeholder="Enter your message"
          value={message}
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}
