import "./Chat.css";
import React, { useState, useEffect } from "react";
import axios from "axios";
import { io } from "socket.io-client";

export default function Chat() {
  const [data, setData] = useState("");
  const [students, setStudents] = useState([]);
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]); // Local messages
  const [getMessages, setGetMessages] = useState([]); // Fetched messages
  const socket = io("http://localhost:9000");

  useEffect(() => {
    Promise.all([
      axios.get("http://localhost:9000/api/data"),
      axios.get("http://localhost:9000/api/students"),
      axios.get("http://localhost:9000/api/messages"),
    ])
      .then(([dataRes, studentRes, messageRes]) => {
        setData(dataRes.data.message);
        setStudents(studentRes.data);
        setGetMessages(messageRes.data); // ✅ Corrected
      })
      .catch((error) => console.log("Error fetching data:", error));

    socket.on("receiveMessage", (message) => {
      console.log("Message received from socket:", message);
      setMessages((prevMessages) => [...prevMessages, message]); // ✅ Realtime updates
    });

    return () => {
      socket.off("receiveMessage"); // Cleanup
      socket.disconnect();
    };
  }, []);

  const HandleMessage = (event) => {
    setMessage(event.target.value);
  };

  const HandleSubmit = (event) => {
    event.preventDefault();
    console.log("Message sent:", message);

    axios
      .post("http://localhost:9000/api/message", { message })
      .then((response) => {
        console.log("Message sent successfully:", response.data);
      })
      .catch((error) => {
        console.log("Error sending message:", error);
      });

    socket.emit("sendMessage", message);
    setMessages((prevMessages) => [...prevMessages, message]); // ✅ Update messages array
    setGetMessages((prevMessages) => [...prevMessages, message]); // ✅ Update fetched messages
    setMessage(""); // Clear input
  };

  return (
    <div className="main">
      <div className="msgBody">
        {/* <h3>Stored Messages:</h3>
        <ul>
          {getMessages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul> */}
        <h3>Live Messages:</h3>
        <ul>
          {messages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul>
      </div>
      <form onSubmit={HandleSubmit}>
        <input
          onChange={HandleMessage}
          type="text"
          placeholder="Enter your message"
          value={message}
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}
