import "./Chat.css";
import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
// import { response } from "express";

export default function Chat() {
  const [data, setData] = useState("");
  const [students, setStudents] = useState([]);
  const [message, setMessage] = useState("");

  useEffect(() => {
    Promise.all([
      axios.get("http://localhost:9000/api/data"),
      axios.get("http://localhost:9000/api/students"),
    ])
      .then(([data, studentData]) => {
        setData(data.data.message);
        setStudents(studentData.data);
      })
      .catch((error) => console.log("Error fetching data:", error));
  }, []);

  const HandleMessage = (event) => {
    setMessage(event.target.value);
  };
  const HandleSubmit = (event) => {
    event.preventDefault();
    console.log("Message sent:", message);

    axios
      .post("http://localhost:9000/api/message", { message })
      .then((response) => {
        console.log("Message sent succefullt:");
      })
      .catch((error) => {
        console.log("Error sending message:", error);
      });
  };

  return (
    <div className="main">
      <div className="msgBody">
        <p>New Message: {message}</p>
      </div>
      <form onSubmit={HandleSubmit}>
        <input
          onChange={HandleMessage}
          type="text"
          placeholder="Enter your message"
        />
        <button>Send</button>
      </form>
    </div>
  );
}
