import "./Chat.css";
import React, { useState, useEffect } from "react";
import axios from "axios";
import { io } from "socket.io-client";

const socket = io("http://localhost:9000");

export default function Chat() {
  const [students, setStudents] = useState([]);
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]); // Stores all messages

  useEffect(() => {
    // Fetch initial messages and students
    Promise.all([
      axios.get("http://localhost:9000/api/students"),
      axios.get("http://localhost:9000/api/messages"),
    ])
      .then(([studentRes, messageRes]) => {
        setStudents(studentRes.data);
        setMessages(messageRes.data); // âœ… Load previous messages
      })
      .catch((error) => console.error("Error fetching data:", error));

    // ðŸ”¥ Listen for new messages
    socket.on("receiveMessage", (newMessage) => {
      console.log("Message received:", newMessage);
      setMessages((prevMessages) => [...prevMessages, newMessage]);
    });

    // ðŸ”¥ Load previous messages when connecting
    socket.on("previousMessages", (oldMessages) => {
      setMessages(oldMessages);
    });

    return () => {
      socket.off("receiveMessage");
      socket.off("previousMessages");
    };
  }, []);

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (!message.trim()) return;

    // Send message to backend
    axios
      .post("http://localhost:9000/api/message", { message })
      .then((response) => {
        console.log("Message sent successfully:", response.data);
      })
      .catch((error) => {
        console.error("Error sending message:", error);
      });

    // Emit message via Socket.IO
    socket.emit("sendMessage", message);
    setMessage(""); // Clear input
    console.log(messages);
  };

  return (
    <div className="main">
      <div className="msgBody">
        <h3>Chat Messages:</h3>
        <ul>
          {messages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul>
      </div>
      <form onSubmit={handleSubmit}>
        <input
          onChange={handleMessageChange}
          type="text"
          placeholder="Enter your message"
          value={message}
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}
